const table_body=document.querySelector("#table-body");const search=document.getElementById("search");async function getAll(){const request=await fetch("https://disease.sh/v3/covid-19/all");const data=await request.json();table_body.innerHTML=`\n    <tr>       \n        <td class="fw-bolder text-success table-warning">World Total</td>\n        <td class="table-warning fw-bold">${data.population}</td>\n        <td class="table-warning fw-bold">${data.cases}</td>\n        <td class="table-warning fw-bold">${data.todayCases}</td>\n        <td class="table-warning fw-bold">${data.deaths}</td>\n        <td class="table-warning fw-bold">${data.todayDeaths}</td>\n        <td class="table-warning fw-bold">${data.recovered}</td>\n        <td class="table-warning fw-bold">${data.todayRecovered}</td>\n        <td class="table-warning fw-bold">${data.active}</td>\n        <td class="table-warning fw-bold">${data.critical}</td>\n        <td class="table-warning fw-bold">${data.tests}</td> \n    </tr>\n    `}getAll();async function getCountries(){const request=await fetch("https://disease.sh/v3/covid-19/countries");const data=await request.json();for(let i=0;i<data.length;i++){table_body.innerHTML+=`\n    <tr class = "country">     \n        <td >${data[i].country}</td>\n        <td>${data[i].population}</td>\n        <td>${data[i].cases}</td>\n        <td>${data[i].todayCases}</td>\n        <td>${data[i].deaths}</td>\n        <td>${data[i].todayDeaths}</td>\n        <td>${data[i].recovered}</td>\n        <td>${data[i].todayRecovered}</td>\n        <td>${data[i].active}</td>\n        <td>${data[i].critical}</td>\n        <td>${data[i].tests}</td>\n    </tr>\n    `}}getCountries();search.addEventListener("keyup",(function(){const filter=search.value.toLowerCase();const countryList=document.querySelectorAll(".country");countryList.forEach((item=>{if(item.textContent.toLowerCase().includes(filter.toLowerCase())){item.style.display=""}else{item.style.display="none"}}))}));